INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (2, 'Hiramoto Akira', 'https://st.ntcdntempv3.com/data/comics/233/prison-school.jpg', '----- Bạn đã từng đọc các bộ truyện về High School----Bạn đã thấy những gì ở đó... tình bạn... tình yêu... chiến tranh... các hoạt động tập thể... vậy bạn đã đọc thể loại High School này chưaTRƯỜNG TRONG TRƯỜNG 5 chàng trai cùng với hơn 1000 bạn gái khác nhưng tiết thay tại sao cùng trường mà trang phục của họ lại khác tình cảnh gì đã xảy ra với họ (--1 Khung Cảnh Trường Học Hoàn Toàn Mới--)', 'Trường học ngục tù', 'Prison School', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (3, '', 'https://st.ntcdntempv3.com/data/comics/187/yume-yume-shinjuu.jpg', 'Yuzuru luôn có một giấc mơ lặp đi lặp lại. Đó là cậu và một cậu trai cùng chơi trong một ngôi đền, Yuzuru không thể nhớ được cậu ấy là ai. Khi Ryosuke - em trai của Yuzuru - giới thiệu bạn cùng lớp tên là Takumi, Yuzuru bỗng nhận ra Takumi chính là cậu trai ấy. Takumi rất vui khi Yuzuru nhớ được cậu. Gặp lại mối tình đầu, Yuzuru lần nữa bị cuốn hút bởi cậu ấy, nhưng...', 'Yume Yume Shinjuu', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (4, '', 'https://st.ntcdntempv3.com/data/comics/204/musashi-kun-to-murayama-san-tsukkiate-mi-7210.jpg', 'Musashi và Miyamura, cả hai đứng ngoài lớp quyết định hò hẹn với nhau! Đi theo chuyện tình lãng mạn, hài hước chậm rãi 4-koma', 'Musashi-Kun To Murayama-San Tsukkiate Mita', 'Musamura ; Musashi-Kun And Murayama-San''s Dating Trial', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (5, '', 'https://st.ntcdntempv3.com/data/comics/186/someday-well-get-there.jpg', 'Minoru là con gái của một võ sĩ. Một ngày ông đề nghị cả nhà đi Las Vegas để thực hiện ước mơ, cô bé nghe thế rất lưỡng lự. Hôm sau, lúc về nhà cô tình cờ bắt gặp một cô gái cùng trường đi vào quán game. Cô gái có tài khoản game là "Shijou" đã hướng dẫn Minoru chơi game và có ước mơ làm một game thủ. Minoru cảm kích trước khát vọng của Shijou nên quyết định theo cha đi Las Vegas, còn Shijou thấy được tài năng của Minoru liền đi tìm cô bé đề nghị cô gia nhập nhóm của mình...Chuyện gì sẽ diễn ra tiếp theo, hãy đón xem :D', 'Someday, We''ll Get There', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (6, 'KAWACHI Izumi', 'https://st.ntcdntempv3.com/data/comics/133/eighth.jpg', 'Eighth Research là một công ty công nghệ sinh học lớn dựa trên nghiên cứu tiên tiến và phát triển của các thao tác DNA. Naoya Graphico là người trong đội ngũ an ninh làm việc cho công ty nghiên cứu của chú mình. Naoya là một người có kỹ năng chiến đấu cao và cậu luôn được giao cho các nhiệm vụ bí mật liên quan đến lợi ích của Eighth Research. Cậu ngươi luôn hoàn thành tốt nhiệm vụ của mình và mỗi nhiệm vụ của cậu đều có liên quan đến một cô, mỗi khi cậu hoàn thành một nhiệm vụ thì đều mang một cô gái về phòng mình. Bên cạnh đó cậu lại là một tên luôn có ham muốn XXX liệu điều đó có gây trở ngại cho nhiệm vụ của cậu không ?', 'Eighth', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (7, 'WATANABE Kazuyuki', 'https://st.ntcdntempv3.com/data/comics/142/ly-do-song-con.jpg', '"Mình không muốn cứ thế này rồi kết thúc"Trong buổi lễ tốt nghiệp trung học, Akira đã quyết định thổ lộ tình cảm của mình với Himeno, cô gái cậu thầm thưong trộm nhớ bấy lâu. Nhưng chờ đợi cậu trong lớp lại không phải kà Himeno mà cậu quen biết! Trước khi kịp nhận ra điều đó , Akira và các bạn cùng học khác đã bị nhốt trong trường học và không thể thoát ra . Ai đã làm điều này? Lí do là gì ? Những thế lực bí ẩn hiện hữu quanh 11 đứa trẻ khốn khổ, như thể đang chế nhạo sự yếu sợ ấy. Chuyện gì đang chờ đợi họ? Tương lai đầy hy vọng hay một kết thúc tuyệt vọng ... ?', 'Lý Do Sống Còn', '', 'Đang tiến hành');
