INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (26, 'DC Comics', 'https://st.ntcdntempv3.com/data/comics/86/supergirl.jpg', 'Câu chuyện về Kara Zor-El, người họ hàng của Superman, người con gái cuối cùng của Krypton. Không như Superman, cô đến Trái Đất khi đã trưởng thành, đồng thời vẫn chưa hề biết về sự hủy diệt của Krypton. Ở một nơi hoàn toàn xa lạ, cô sẽ phải làm gì để hòa nhập, để kiểm soát sức mạnh của mình?', 'Supergirl', '', 'Đang tiến hành');
