INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (65, 'FUKUI Harutoshi& SHIMOTSUKI Kayoko', 'https://st.ntcdntempv3.com/data/comics/237/c-blossom-case-729.jpg', 'Thế giới của Matsumiya Kana sụp đổ khi cha cô bị bắt vì tội biển thủ công quỹ quốc gia! Sau đó cô lại dính vào vụ bắt cóc để cha cô không thể đứng ra làm nhân chứng vạch mặt tên tội phạm thật sự đã vu cáo cho cha cô! Một người đàn ông tên Kisaragi Kou đã cứu cô và đó là ai!?', 'C-BLOSSOM - CASE 729', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (66, 'Watanabe Taeko', 'https://st.ntcdntempv3.com/data/comics/202/family.jpg', 'Truyện mở ra vào một buổi tối mùa thu, trong cuộc sum họp mỗi tối của gia đình Anderson sống tại Minami, bang Florida, Mĩ. Cha, anh trai cả, chị gái và em gái út đang thảo luận về việc lựa chọn chương trình tivi tối nay... Thì bất ngờ nghe tiếng chuông cửa của một cậu bé 5 tuổi, đem theo một chú chó lông xù, và tự nhận là con rơi của ông Anderson. Sự xuất hiện một cách bất thường của cậu bé đã kéo theo biết bao câu chuyện khiến người đọc phải vừa cười vừa khóc... (TonomiHyura - Trích 4AM)', 'Family!', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (67, '', 'https://st.ntcdntempv3.com/data/comics/196/cloverfieldkishin.jpg', 'Với những ai đã xem qua về vũ trụ Cloverfield chắc không còn xa lạ với cụm từ này. Nhưng Cloverfield là một vũ trụ rất phức tạp mà chỉ ba phần phim đã ra mắt thì không thể cho ta biết hết về những bí mật trong đó đc. Cloverfield Kishin lấy bối cảnh ở Nhật Bản khi thảm họa Cloverfield xảy ra. Cho ta biết về một cuộc đời, một con người nhỏ bé nhưng có thể thay đổi cả thế giới. Ngoài ra nó còn mang đến bài học về bạo lực học đường về việc xem những con người thấp bé hơn mình là kì dị, là quái vật. Biết đâu một ngày vì thế mà họ thật sự trở thành quái vật thì sao.', 'Cloverfield/Kishin', '', 'Đang tiến hành');
