INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (73, '', 'https://st.ntcdntempv3.com/data/comics/254/bien-nien-su-han-thu-chronicles-of-hate.jpg', 'Ở một thế giới mà mặt trời bị đông cứng và mặt trăng bị nung chảy, một người hùng không tưởng trỗi dậy, cứu lấy Mẹ Trái Đất khỏi xiềng xích của mình. Con đường của anh ta đi là một con đường u tối đầy chông gai với vô số đạo quân của kẻ thù đón chờ. Một bộ truyện đầy chất ám ảnh và đen tối với những hình ảnh gai góc rùng rợn cùng cực', 'Biên Niên Sử Hận Thù - Chronicles of Hate', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (74, '', 'https://st.ntcdntempv3.com/data/comics/33/sao-doi-ngoi.jpg', 'Eun Bi là 1 người mẫu CF nổi tiếng của Hàn Quốc, tình cờ cô và bạn thân Joo Yin đi Trung Quốc chơi và mua được 1 tấm vải cổ, ngày xưa của Dương Quý Phi, cả 2 bị tấm vải đó cuốn vào quá khứ thời đại của Dương Quý Phi, ở đây quan niệm về sắc đẹp hoàn toàn khác với hiện đại, theo họ người đẹp là tròn trịa đẫy đà. Người mẫu có thân hình như Eun Bu bị coi là xấu xí .. và sau đó là biết bao nhiêu chuyện xảy ra khi cả hai cô gái cùng nhau tìm cách đi tìm miếng vải để trở về hiện tại, cũng như mối tình của Eun Bi và chàng thái tử đẹp trai con của Đường Huyền Tông.', 'Sao Đổi Ngôi', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (75, 'Harada Anjyu', 'https://st.ntcdntempv3.com/data/comics/253/inukai-x-tribe.jpg', 'Trên thế giới, có rất nhiều sinh vật nhìn giống như con người bình thường nhưng thực chất chúng sở hữu những năng lực kì lạ. Đôi khi chúng sử dụng sức mạnh đó để phạm tội và gây rối. Để ngăn chặn điều đó, một biệt đội đã được thành lập bởi những người có khả năng tương tự. Một câu chuyện về những bí ẩn siêu nhiên, kinh dị, pha chút trinh thám và biệt đôi "tăng động" xin được phép mở màn...', 'Inukai x Tribe', 'イヌカイ×トライヴ; Inukai Tribe', 'Đang tiến hành');
