INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (99, 'Kim Hee Eun', 'https://st.ntcdntempv3.com/data/comics/37/tieu-thu-hau-dau.jpg', 'Bối cảnh câu chuyện là ngôi trường danh giá dành cho con cái các gia đình vương tôn quý tộc vào thời mà quyền lực của hoàng gia là tuyệt đối. Sinh ra trong gia đình gia giáo March nhưng Elizabeth lại khác hẳn các chị em và cha mẹ của mình. Cô không thông minh, trang nhã như chị cả Margaret; không giỏi thể thao, anh tú như chị hai Josephine; và không phải là một họa sĩ thiên tài, xinh đẹp như cô em út Amy. Elizabeth vẫn hay bị những người ác miệng gọi là con vịt xấu xí trong gia đình thiên nga và bản thân cô cũng dần nghĩ về mình như thế. Elizabeth luôn muốn thoát ra khỏi cuộc sống của mình để tìm đến với tự do, nhưng cứ mỗi lần cô định cố gắng thì lại bị "xui xẻo" kìm hãm. Đặc biệt, sự xui xẻo lớn nhất đời mà định mệnh trao cho cô mang tên Adrian Avery - một công tử quý tộc danh giá bậc nhất cũng nhập học ở trường của cô. Adrian Avery nổi tiếng tài hoa, uyên bác, phong độ cho tới khi Elizabeth biết được bộ mặt thật lạnh lùng, kiêu ngạo của cậu ta. Tuy nhiên, Adrian lại là chàng trai duy nhất khơi gợi được sự tự tin và những cảm xúc giấu kín trong con tim khao khát tự do của Elizabeth bởi chính bản thân cậu cũng không chịu nổi cuộc sống giả tạo, nhàm chán và tù túng của gia đình mình. Hai con người yêu tự do đến với nhau do định mệnh nhưng cuộc tình đó lại đi ngược với lý thuyết "môn đăng hộ đối" của xã hội đương thời. Tình yêu có thể vượt qua không gian, thời gian nhưng khi gia đình, cuộc sống, xã hội trở thành vật cản thì còn cần nhiều hơn một trái tim để vượt qua nó...', 'Tiểu thư hậu đậu', 'Cô Bạn Của Tôi; Dear My Girls', 'Đang tiến hành');
