INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (114, '', 'https://st.ntcdntempv3.com/data/comics/152/spider-man-noir.jpg', 'Câu truyện của Spider-man Noir đặt vào bối cảnh những năm 30, thuộc vũ trụ Marvel Noir (khác với vũ trụ nguyên bản là Marvel-616). Trong Marvel Noir, các siêu anh hùng hầu hết không có siêu sức mạnh hoặc sức mạnh giảm đi khá nhiều (Spider-man), nhưng họ vẫn có thừa lòng can đảm và chính trực để đánh bại kẻ ác.Cốt truyện của Marvel Noir thường u ám hơn vì đi theo phong cách dòng phim tội phạm thập niên 30.', 'Spider-man Noir', '', 'Đang tiến hành');
