INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (133, 'Fujiko F. Fujio', 'https://st.ntcdntempv3.com/data/comics/192/tuyen-tap-truyen-ngan-cua-tac-gia-doraem-2280.jpg', 'Nếu bạn đã quen với thế giới truyện tranh của Fujiko F. Fujio qua các bộ manga cho trẻ em, thì hãy một lần đọc thử truyện sensei khi ông vẽ dành cho người lớn nhé.Truy cập fanpage “Nhật ngữ Hachi – dạy & học tiếng Nhật tại Hải Phòng” hoặc “Nhật ngữ Hachi – tiếng Nhật online miễn phí” để đọc trước 3 chap và ủng hộ mình nhé ^^', 'Tuyển tập truyện ngắn của tác giả Doraemon', '', 'Đang tiến hành');
