INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (135, 'Fujishiro Takeshi', 'https://st.ntcdntempv3.com/data/comics/37/nagasarete-airantou.jpg', 'Ikuto Touhohin vừa có một cuộc chiến cuối cùng với con người cũ của mình, trong lúc đó cậu đã dẫn đến quyết định bỏ nhà đi bụi. Cậu lên một tàu, quyết định để có một kỳ nghỉ, nhưng tàu đột nhiên trúng một cơn bão lớn.Khi cậu tỉnh lại, cậu nhận ra cậu vẫn còn sống ... trên một hòn đảo. Một hòn đảo bị cô lập. Một hòn đảo bị cô lập những cô gái. Mắc kẹt trên một hòn đảo với cô gái là duy nhất, không có điện, khí đốt, phát thanh, truyền hình ... dường như cậu đã trở lại thời kì đồ đá...', 'Nagasarete Airantou', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (136, '', 'https://st.ntcdntempv3.com/data/comics/150/yakedo-shoujo.png', 'I''m dying.The sun is shines brightly in the sky, buring my eye.I''m scream, "sun, surely you don''t intend to shine this bright!".The sun still shining ever so brightly.Bataille, Georges. 1943. Inner Experrience.Một tác phẩm của Kantetsu, Prison lab.', 'Yakedo Shoujo', 'Yakedo Syojo', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (137, 'Ueda Satoshi', 'https://st.ntcdntempv3.com/data/comics/195/flags.jpg', 'Ouka là một đế quốc thống trị hơn một phần ba thế giới. Nhằm tránh xung đột với các nước khác, họ đã tổ chức những cuthi đấu 8 năm một lần. Trong cuộc thi, 6 ứng cử viên- Được biết đến như những người yêu nước- Đến từ mỗi quốc gia sẽ chiến đấu vơ các đội từ những nước khác và những ngư chiến thắng sẽ có được DANH TIẾNG và DANH DỰ. Câu chuyện theo chân Kurou, một cậu bé bước đi theo con đường giống với cha của mình để trở thành một người yêu nước mạnh mẽ nhất cùng với đồng đội của mình.', 'Flags', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (138, 'Kuraishi Yuu', 'https://st.ntcdntempv3.com/data/comics/5/phao-dai-cua-sach-khai-huyen.jpg', 'Thể loại đánh zombie siêu kinh điển đậm chất Deathplace!Đảm bảo truyện này không hay ko gay cấn không ăn tiền! Cố gắng bám sát bản Eng luôn! Na ná High School of Dead nhưng mà ko có Ecchi!mấy thým ủng hộ nhiệt tình cái coi nào!.', 'Pháo đài của Sách Khải Huyền', 'Fortress of the Apocalypse; Apocalypse no Toride', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (139, '', 'https://st.ntcdntempv3.com/data/comics/210/battle-mexia.jpg', 'Battle Mexia , một Game đối kháng nhập vai hoàn hảo, mọi hành động và cảm giác đều như thật, Trong những năm ở tương lai khi Game chi phối mọi tầng lớp - nếu bạn là một dân chuyên nhà nước sẽ nuôi bạn cả đời - Main chính nhà ta sẽ ra sao khi nhập học vào trường lấy Battle Mexia làm điểm chính, mọi người đoán xem nhé!', 'Battle Mexia', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (140, 'Nhiều tác giả', 'https://st.ntcdntempv3.com/data/comics/91/assassins-creed.jpg', 'Charlotte de la Cruz, một cô gái bình thường sống ở San Diego với một công việc tầm thường. Đúng vậy, cô gái này chả có cái mẹ gì đặc biệt cả, trừ việc cô đang bị bọn Templar săn đuổi... à quên, và tổ tiên cô là một sát thủ sống vào thời kì săn phù thuỷ ở Salem.', 'Assassin''s Creed', 'AC', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (141, '', 'https://st.ntcdntempv3.com/data/comics/33/matano-okoshiwo.jpg', 'Ari Tsubate là một cậu bé 15 tuổi, làm gì cũng tệ. Một ngày nọ sau khi nhận được điểm kiểm tra, cậu đã chết trong một vụ tai nạn. Khi tỉnh dậy, cậu thấy mình đang ở ngay cổng vào của Quán trọ linh hồn. Cùng với sự chào đón của các nhân viên của quán trọ, cậu đã bắt đầu cuộc hành trình của mình tại thế giới linh hồn.', 'Matano Okoshiwo', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (142, 'Inose', 'https://st.ntcdntempv3.com/data/comics/200/soi-den-va-cao-bac.jpg', 'Kuzunoha là một chú Cáo trông rất bình thường đang học tại trường dành cho thú nhân. Ở trường, ngoài hội Cáo còn có hội Mèo và hội Sói.Hội Sói có truyền thống mà Kuzunoha vô cùng sợ hãi - nghi thức lột da các loài thú có bộ lông đẹp .Tại sao lại như thế?Mọi bí ẩn sẽ dần được giải đáp khi nhân vật chính của chúng ta gặp gỡ và kết bạn với những người bạn khác loài.', 'Sói Đen Và Cáo Bạc', '黒狼と金狐 , 黒狼と銀狐 , Black Wolf and Silver Fox , Kuro Ookami to Kin Kitsune , Kuroookami to Kin Kitsune , Kuroo Okami to Kinko', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (143, 'Katou Tomoo', 'https://st.ntcdntempv3.com/data/comics/110/ichigo-to-anzu.jpg', 'Hai chị em sinh đôi với những nét tính cách rất khác biệt: Ichigo và Anzu cùng chú chó Melon tinh nghịch sẽ đem đến cho bạn một bộ shoujo manga vô cùng dễ thương cho mùa hè này. Mọi chuyện rối tinh lên khi mẹ của hai chị em muốn tái hôn với bác sĩ Asano và bị Ichigo - một cô bé bị “dị ứng con trai” phản đối kịch liệt…Đón đọc câu chuyện tình cảm đậm chất gia đình của tác giả Kato Tomoo', 'Ichigo to Anzu', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (144, 'Mitsunaga Yasunori', 'https://st.ntcdntempv3.com/data/comics/27/khi-anh-hung-moi-chinh-la-ac-quy.jpg', 'Có 2 thế lực đang tồn tại, "Lũ ác quỷ" đe dọa nhân loại và "Anh hùng" là những kẻ duy nhất có thể tiêu diệt chúng! Nhưng “Anh hùng” có thực sự đứng về phía nhân loại ...! ?? Hideo,nhân vật chính là một cậu bé từng được một anh hùng cứu mạng,sau khi điều tra được rõ chân tướng sự việc về vụ tai nạn của cậu,đã bị Anh hùng tìm cách bịt miệng thủ tiêu.....đồng tác giả với bộ time stop brave', 'Khi Anh Hùng Mới Chính Là Ác Quỷ!!', 'Evil Heros, Anh Hùng Quỷ Dữ', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (145, 'Yoneshiro Kyo', 'https://st.ntcdntempv3.com/data/comics/200/toi-la-cho.jpg', 'Điều gì đang chờ đón Yuuki Hajime, một chàng trai làm việc tại một công ty cổ phần dịch vụ bạn bè cho thuê khi đến nhà của Kumoi Yumeko, khách hàng của mình?Một bộ truyện với nội dung xoay quanh mối quan hệ chủ - tớ cùng những tình tiết vặn xoắn tâm lý lẫn trái tim người đọc của tác giả Yoneshiro Kyo, nổi danh với tác phẩm "Ageku no Hate no Kanon" (Kanon - Tận Cùng Của Tình Yêu).', 'Tôi Là Chó', 'Boku wa Inu', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (146, '', 'https://st.ntcdntempv3.com/data/comics/84/byakuya-zaushi.jpg', 'Tuyển tập 1 vol về những câu chuyện siêu nhiên u tối. Những linh hồn sống, quỷ dữ, cái chết, luân hồi là chủ đề chính trong những câu chuyện này', 'Byakuya Zaushi', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (147, 'FUKUI Harutoshi& SHIMOTSUKI Kayoko', 'https://st.ntcdntempv3.com/data/comics/237/c-blossom-case-729.jpg', 'Thế giới của Matsumiya Kana sụp đổ khi cha cô bị bắt vì tội biển thủ công quỹ quốc gia! Sau đó cô lại dính vào vụ bắt cóc để cha cô không thể đứng ra làm nhân chứng vạch mặt tên tội phạm thật sự đã vu cáo cho cha cô! Một người đàn ông tên Kisaragi Kou đã cứu cô và đó là ai!?', 'C-BLOSSOM - CASE 729', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (148, 'Watanabe Taeko', 'https://st.ntcdntempv3.com/data/comics/202/family.jpg', 'Truyện mở ra vào một buổi tối mùa thu, trong cuộc sum họp mỗi tối của gia đình Anderson sống tại Minami, bang Florida, Mĩ. Cha, anh trai cả, chị gái và em gái út đang thảo luận về việc lựa chọn chương trình tivi tối nay... Thì bất ngờ nghe tiếng chuông cửa của một cậu bé 5 tuổi, đem theo một chú chó lông xù, và tự nhận là con rơi của ông Anderson. Sự xuất hiện một cách bất thường của cậu bé đã kéo theo biết bao câu chuyện khiến người đọc phải vừa cười vừa khóc... (TonomiHyura - Trích 4AM)', 'Family!', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (149, '', 'https://st.ntcdntempv3.com/data/comics/196/cloverfieldkishin.jpg', 'Với những ai đã xem qua về vũ trụ Cloverfield chắc không còn xa lạ với cụm từ này. Nhưng Cloverfield là một vũ trụ rất phức tạp mà chỉ ba phần phim đã ra mắt thì không thể cho ta biết hết về những bí mật trong đó đc. Cloverfield Kishin lấy bối cảnh ở Nhật Bản khi thảm họa Cloverfield xảy ra. Cho ta biết về một cuộc đời, một con người nhỏ bé nhưng có thể thay đổi cả thế giới. Ngoài ra nó còn mang đến bài học về bạo lực học đường về việc xem những con người thấp bé hơn mình là kì dị, là quái vật. Biết đâu một ngày vì thế mà họ thật sự trở thành quái vật thì sao.', 'Cloverfield/Kishin', '', 'Đang tiến hành');
