INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (161, '', 'https://st.ntcdntempv3.com/data/comics/175/hitoribocchi-no-chikyuu-shinryaku.jpg', 'Sơ lược:Kouichi bước vào trường cấp 3 với 1 mục đích: Kế nghiệp ông làm việc ở 1 quán cafe. Nhưng ngày đầu tiên ở trường, 1 cô gái lạ mặt đeo chiếc mặt nạ kì quái đã đuổi theo cậu muốn đòi trái tim cậu. Trái tim mà đang ở trong lồng ngực cậu là của cô ta. Và cô ta là người ngoài hành tinh đến xâm chiếm trái đất...', 'Hitoribocchi No Chikyuu Shinryaku', '', 'Đang tiến hành');
INSERT INTO mangadb.mangas (id, author, cover, description, name, other_name, status) VALUES (162, 'Yamada Yoshinobu', 'https://st.ntcdntempv3.com/data/comics/161/deathtopia.jpg', 'Tai nạn xảy đến với Koh Fujimura khiến cậu có nguy cơ mất đi cả đôi mắt, nhưng sau cuộc phẫu thuật thần kì mắt cậu đã sáng lại và còn kèm theo khả năng đặc biệt... Thêm vào đó, một người phụ nữ lạ xuất hiện, và cuộc đời cậu thay đổi chỉ qua một đêm... Rồi sau, những chuyện kì lạ liên tục xảy đến...', 'Deathtopia', '', 'Đang tiến hành');
